import { Button } from '@material-ui/core';
import Contact from './Contact'
function Description(){
    function openContact(){console.log("hello")
    }
    return (
        <div id="portfolio" className= "description">
        <p className="intro">Hello,I'm <span className="name">Peris Chepkemoi</span> am a website and software developer.</p>
        <p className="intro"> Am focused on creating nice and user friendly softwares.</p>
        <Button variant="contained"  size="large" onClick={openContact}>Get in Touch</Button>
       </div>
     
    )
}
export default Description
